import React, { useState, useEffect } from 'react';
import { Button, Form, Input } from 'antd';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import './HallDetail.css';

interface DailyData {
  day: number;
  feedConsumption: number;
  waterConsumption: number;
  averageWeight: number;
  mortality: number;
}

interface RossStandardData {
  day: number;
  weightKg: number;
  feedConsumptionGram: number;
  waterConsumptionLiter: number;
  mortalityCount: number;
}

const HallDetail: React.FC = () => {
  const [dailyData, setDailyData] = useState<DailyData[]>([]);
  const [rossStandardData, setRossStandardData] = useState<RossStandardData[]>([]);
  const [currentDay, setCurrentDay] = useState(1);

  useEffect(() => {
    // Initialize daily data and standard data
    setDailyData([
      { day: 1, feedConsumption: 1.2, waterConsumption: 2.5, averageWeight: 0.5, mortality: 2 },
      { day: 2, feedConsumption: 1.5, waterConsumption: 3.0, averageWeight: 0.7, mortality: 1 },
    ]);
    setRossStandardData([
      { day: 1, weightKg: 0.5, feedConsumptionGram: 1200, waterConsumptionLiter: 2.5, mortalityCount: 2 },
      { day: 2, weightKg: 0.7, feedConsumptionGram: 1500, waterConsumptionLiter: 3.0, mortalityCount: 1 },
    ]);
  }, []);

  const handleDailySubmit = (values: any) => {
    setDailyData((prevData) =>
      prevData.map((data) =>
        data.day === currentDay ? { ...data, ...values } : data
      )
    );
  };

  const goToPreviousDay = () => setCurrentDay((prevDay) => Math.max(prevDay - 1, 1));
  const goToNextDay = () => setCurrentDay((prevDay) => Math.min(prevDay + 1, 56));

  const dailyDataForCurrentDay: DailyData = dailyData.find((data) => data.day === currentDay) || {
    day: currentDay,
    feedConsumption: 0,
    waterConsumption: 0,
    averageWeight: 0,
    mortality: 0,
  };

  const standardDataForCurrentDay: RossStandardData = rossStandardData.find((data) => data.day === currentDay) || {
    day: currentDay,
    weightKg: 0,
    feedConsumptionGram: 0,
    waterConsumptionLiter: 0,
    mortalityCount: 0,
  };

  const chartData = {
    labels: dailyData.map((data) => `Day ${data.day}`),
    datasets: [
      {
        label: 'Feed Consumption (kg)',
        data: dailyData.map((data) => data.feedConsumption),
        borderColor: '#4CAF50', // سبز
        backgroundColor: 'rgba(76, 175, 80, 0.2)',
        tension: 0.3,
        borderWidth: 2,
      },
      {
        label: 'Water Consumption (liters)',
        data: dailyData.map((data) => data.waterConsumption),
        borderColor: '#2196F3', // آبی
        backgroundColor: 'rgba(33, 150, 243, 0.2)',
        tension: 0.3,
        borderWidth: 2,
      },
    ],
  };

  return (
    <div className="modern-hall-detail-container">
      <div className="modern-header">
        <h2>Hall Details for Day {currentDay}</h2>
        <div className="modern-day-navigation">
          <Button 
            onClick={goToPreviousDay} 
            disabled={currentDay === 1}
            className="modern-nav-button"
          >
            Previous Day
          </Button>
          <span className="modern-day-indicator">Day {currentDay}</span>
          <Button 
            onClick={goToNextDay} 
            disabled={currentDay === 56}
            className="modern-nav-button"
          >
            Next Day
          </Button>
        </div>
      </div>

      <div className="modern-chart-container">
        <Line 
          data={chartData} 
          options={{
            responsive: true,
            plugins: {
              legend: {
                position: 'top',
                labels: {
                  font: {
                    size: 14
                  }
                }
              },
            },
            scales: {
              y: {
                beginAtZero: true,
                grid: {
                  color: 'rgba(0,0,0,0.05)'
                }
              },
              x: {
                grid: {
                  color: 'rgba(0,0,0,0.05)'
                }
              }
            }
          }}
        />
      </div>

      <div className="modern-form-section">
        <Form
          onFinish={handleDailySubmit}
          initialValues={dailyDataForCurrentDay}
          layout="vertical"
          className="modern-data-form"
        >
          <div className="modern-form-grid">
            <Form.Item 
              label="Feed Consumption (kg)" 
              name="feedConsumption" 
              rules={[{ required: true }]}
              className="modern-form-item"
            >
              <Input type="number" className="modern-input" />
            </Form.Item>
            <Form.Item 
              label="Water Consumption (liters)" 
              name="waterConsumption" 
              rules={[{ required: true }]}
              className="modern-form-item"
            >
              <Input type="number" className="modern-input" />
            </Form.Item>
            <Form.Item 
              label="Average Weight (kg)" 
              name="averageWeight" 
              rules={[{ required: true }]}
              className="modern-form-item"
            >
              <Input type="number" className="modern-input" />
            </Form.Item>
            <Form.Item 
              label="Mortality" 
              name="mortality" 
              rules={[{ required: true }]}
              className="modern-form-item"
            >
              <Input type="number" className="modern-input" />
            </Form.Item>
          </div>
          <Form.Item className="modern-submit-item">
            <Button type="primary" htmlType="submit" className="modern-submit-button">
              Submit Data
            </Button>
          </Form.Item>
        </Form>

        <div className="modern-standard-data">
          <h3 className="modern-standard-title">Standard Data for Day {currentDay}</h3>
          <div className="modern-data-grid">
            <div className="modern-data-item">
              <span className="modern-data-label">Weight (kg):</span>
              <span className="modern-data-value">{standardDataForCurrentDay.weightKg}</span>
            </div>
            <div className="modern-data-item">
              <span className="modern-data-label">Feed Consumption (g):</span>
              <span className="modern-data-value">{standardDataForCurrentDay.feedConsumptionGram}</span>
            </div>
            <div className="modern-data-item">
              <span className="modern-data-label">Water Consumption (liters):</span>
              <span className="modern-data-value">{standardDataForCurrentDay.waterConsumptionLiter}</span>
            </div>
            <div className="modern-data-item">
              <span className="modern-data-label">Mortality:</span>
              <span className="modern-data-value">{standardDataForCurrentDay.mortalityCount}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HallDetail;
