import React, { useState, useEffect } from 'react';
import { Button, Form, Input, Table } from 'antd';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import './HallDetail.css';

interface DailyData {
  day: number;
  feedConsumption: number;
  waterConsumption: number;
  averageWeight: number;
  mortality: number;
}

interface RossStandardData {
  day: number;
  weightKg: number;
  feedConsumption: number;
  waterConsumption: number;
  mortality: number;
}

const HallDetail: React.FC = () => {
  const [dailyData, setDailyData] = useState<DailyData[]>([]);
  const [rossStandardData, setRossStandardData] = useState<RossStandardData[]>([]);
  const [currentDay, setCurrentDay] = useState(1);

  const hallName = "Hall A";

  useEffect(() => {
    setDailyData([
      { day: 1, feedConsumption: 1.2, waterConsumption: 2.5, averageWeight: 0.5, mortality: 2 },
      { day: 2, feedConsumption: 1.5, waterConsumption: 3.0, averageWeight: 0.7, mortality: 1 },
    ]);
    setRossStandardData([
      { day: 1, weightKg: 0.5, feedConsumption: 1.2, waterConsumption: 2.5, mortality: 2 },
      { day: 2, weightKg: 0.7, feedConsumption: 1.5, waterConsumption: 3.0, mortality: 1 },
    ]);
  }, []);

  const handleDailySubmit = (values: any) => {
    setDailyData((prevData) =>
      prevData.map((data) =>
        data.day === currentDay ? { ...data, ...values } : data
      )
    );

    if (currentDay < dailyData.length) {
      setCurrentDay(currentDay + 1);
    } else {
      console.log('You are on the last day!');
    }
  };

  const goToPreviousDay = () => setCurrentDay((prevDay) => Math.max(prevDay - 1, 1));
  const goToNextDay = () => setCurrentDay((prevDay) => Math.min(prevDay + 1, dailyData.length));

  const goToAdminPanel = () => {
    window.location.href = 'http://localhost:3000/dashboard/halls';
  };

  const tableData = dailyData.map((data, index) => {
    const standard = rossStandardData[index] || {};
    return {
      key: data.day,
      day: data.day,
      feedConsumption: data.feedConsumption,
      feedConsumptionStandard: standard.feedConsumption || 0,
      waterConsumption: data.waterConsumption,
      waterConsumptionStandard: standard.waterConsumption || 0,
      averageWeight: data.averageWeight,
      averageWeightStandard: standard.weightKg || 0,
      mortality: data.mortality,
      mortalityStandard: standard.mortality || 0,
    };
  });

  const columns = [
    {
      title: 'Day',
      dataIndex: 'day',
      key: 'day',
    },
    {
      title: 'Feed Consumption (kg)',
      dataIndex: 'feedConsumption',
      key: 'feedConsumption',
      render: (value: number, record: any) => (
        <span className="feed-consumption-cell">
          {value} / <strong>{record.feedConsumptionStandard}</strong>
        </span>
      ),
    },
    {
      title: 'Water Consumption (liters)',
      dataIndex: 'waterConsumption',
      key: 'waterConsumption',
      render: (value: number, record: any) => (
        <span className="water-consumption-cell">
          {value} / <strong>{record.waterConsumptionStandard}</strong>
        </span>
      ),
    },
    {
      title: 'Average Weight (kg)',
      dataIndex: 'averageWeight',
      key: 'averageWeight',
      render: (value: number, record: any) => (
        <span className="average-weight-cell">
          {value} / <strong>{record.averageWeightStandard}</strong>
        </span>
      ),
    },
    {
      title: 'Mortality',
      dataIndex: 'mortality',
      key: 'mortality',
      render: (value: number, record: any) => (
        <span className="mortality-cell">
          {value} / <strong>{record.mortalityStandard}</strong>
        </span>
      ),
    },
  ];

  return (
    <div className="hall-detail-container">
      <div className="top-bar">
        <Button 
          onClick={goToAdminPanel} 
          className="admin-panel-btn"
        >
          ← Back to Admin Panel
        </Button>
        <h2 className="hall-title">
          {hallName} - Day {currentDay} Dashboard
        </h2>
      </div>

      <div className="content-grid">
        <div className="card day-navigation-card">
          <div className="day-nav-buttons">
            <Button 
              onClick={goToPreviousDay} 
              disabled={currentDay === 1}
              className="nav-btn"
            >
              ← Previous
            </Button>
            <div className="day-indicator">
              <span className="day-label">CURRENT DAY</span>
              <span className="day-number">{currentDay}</span>
            </div>
            <Button 
              onClick={goToNextDay} 
              disabled={currentDay === dailyData.length}
              className="nav-btn"
            >
              Next →
            </Button>
          </div>
        </div>

        <div className="card form-card">
          <h3 className="card-title">Daily Data Entry</h3>
          <Form 
            onFinish={handleDailySubmit} 
            initialValues={dailyData[currentDay - 1]} 
            layout="vertical"
          >
            <Form.Item 
              label="Feed Consumption (kg)" 
              name="feedConsumption" 
              rules={[{ required: true }]}
              className="form-item"
            >
              <Input type="number" className="form-input" />
            </Form.Item>
            <Form.Item 
              label="Water Consumption (liters)" 
              name="waterConsumption" 
              rules={[{ required: true }]}
              className="form-item"
            >
              <Input type="number" className="form-input" />
            </Form.Item>
            <Form.Item 
              label="Average Weight (kg)" 
              name="averageWeight" 
              rules={[{ required: true }]}
              className="form-item"
            >
              <Input type="number" className="form-input" />
            </Form.Item>
            <Form.Item 
              label="Mortality" 
              name="mortality" 
              rules={[{ required: true }]}
              className="form-item"
            >
              <Input type="number" className="form-input" />
            </Form.Item>
            <Button 
              type="primary" 
              htmlType="submit" 
              className="submit-btn"
              block
            >
              Save Daily Data
            </Button>
          </Form>
        </div>

        <div className="card charts-card">
          <h3 className="card-title">Performance Metrics</h3>
          <div className="charts-container">
            <div className="chart-placeholder">
              [Weight Growth Chart]
            </div>
            <div className="chart-placeholder">
              [Consumption Comparison]
            </div>
          </div>
        </div>

        <div className="card table-card">
          <h3 className="card-title">Daily Data Comparison</h3>
          <Table 
            dataSource={tableData} 
            columns={columns} 
            pagination={false}
            className="data-table"
            bordered
          />
        </div>
      </div>
    </div>
  );
};

export default HallDetail;
