import React, { useState } from 'react';
import { Hall } from '../HallTypes';
import HallModal from '../HallModal/HallModal';
import HallCard from '../';
import './Halls.css';

const Halls: React.FC = () => {
  const [halls, setHalls] = useState<Hall[]>([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleAddHall = (newHall: Hall) => {
    setHalls([...halls, newHall]);
    setIsModalOpen(false);
  };

  const handleDeleteHall = (id: string) => {
    setHalls(halls.filter(hall => hall.id !== id));
  };

  return (
    <div className="halls-container">
      <h2>Halls Management</h2>
      <div className="halls-grid">
        {/* Plus Card */}
        <div className="hall-card plus-card" onClick={() => setIsModalOpen(true)}>
          <div className="plus-icon">+</div>
          <p>Add New Hall</p>
        </div>

        {/* Existing Hall Cards */}
        {halls.map(hall => (
          <HallCard 
            key={hall.id}
            hall={hall}
            onDelete={handleDeleteHall}
          />
        ))}
      </div>

      {/* Modal for adding new hall */}
      <HallModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSubmit={handleAddHall}
      />
    </div>
  );
};

export default Halls;
