import React, { useState } from 'react';
import { Button, Form, Input } from 'antd';

interface HallDetailProps {
    hallName: string;
}

const HallDetail: React.FC<HallDetailProps> = ({ hallName }) => {
    const [currentDay, setCurrentDay] = useState(1);
    const [dailyData, setDailyData] = useState<Record<number, any>>({});

    const handleNextDay = () => {
        if (currentDay < 58) setCurrentDay(currentDay + 1);
    };

    const handlePreviousDay = () => {
        if (currentDay > 1) setCurrentDay(currentDay - 1);
    };

    const handleSubmit = (values: any) => {
        setDailyData({ ...dailyData, [currentDay]: values });
        console.log('Daily Data:', dailyData);
    };

    return (
        <div>
            <h2>Daily Details for Hall: {hallName}</h2>
            <div>
                <Button onClick={handlePreviousDay} disabled={currentDay === 1}>
                    Previous Day
                </Button>
                <span>Day {currentDay}</span>
                <Button onClick={handleNextDay} disabled={currentDay === 58}>
                    Next Day
                </Button>
            </div>
            <Form onFinish={handleSubmit}>
                <Form.Item label="Feed Consumption (kg)" name="feedConsumption" rules={[{ required: true }]}>
                    <Input type="number" />
                </Form.Item>
                <Form.Item label="Water Consumption (liters)" name="waterConsumption" rules={[{ required: true }]}>
                    <Input type="number" />
                </Form.Item>
                <Form.Item label="Mortality" name="mortality" rules={[{ required: true }]}>
                    <Input type="number" />
                </Form.Item>
                <Button type="primary" htmlType="submit">Submit</Button>
            </Form>
        </div>
    );
};

export default HallDetail;
