import { useState } from 'react';
import { Hall } from '../HallTypes';
import '../hall/Halls.css';

interface HallCardProps {
    hall: Hall;
    onDelete: (id: string) => void;
}

const HallCard: React.FC<HallCardProps> = ({ hall, onDelete }) => {
    const [showDetails, setShowDetails] = useState(false);
    const [showDailyInfo, setShowDailyInfo] = useState(false);
    const [dailyInfo, setDailyInfo] = useState<string | null>(null);

    const fetchDailyInfo = async (hallName: string) => {
        // Replace this URL with the actual endpoint to fetch daily info for the hall
        const response = await fetch(`/api/daily-info?hallName=${encodeURIComponent(hallName)}`);
        const data = await response.json();
        setDailyInfo(data.info); // Assuming `data.info` contains the daily information
    };

    const toggleDailyInfo = () => {
        if (!showDailyInfo) {
            fetchDailyInfo(hall.hallName);
        }
        setShowDailyInfo(!showDailyInfo);
    };

    return (
        <div className="hall-card">
            <div className="card-header" onClick={() => setShowDetails(!showDetails)}>
                <h3><strong>Hall Name:</strong> {hall.hallName}</h3>
                <span>{showDetails ? '▲ View Less' : '▼ View More'}</span>
            </div>

            {showDetails && (
                <div className="card-details">
                    <p><strong>Importer:</strong> {hall.importerName}</p>
                    <p><strong>Chickens:</strong> {hall.numberOfChickens.toLocaleString()}</p>
                    <p><strong>Initial Weight:</strong> {hall.initialWeight} kg</p>
                    <p><strong>Budget:</strong> {hall.budgetType}</p>
                    <p><small>Created: {new Date(hall.createdAt).toLocaleDateString()}</small></p>

                    <div className="card-actions">
                        <button 
                            className="edit-button" 
                            onClick={toggleDailyInfo}
                        >
                            {showDailyInfo ? 'Hide Daily Info' : 'Show Daily Info'}
                        </button>
                        <button 
                            className="delete-button" 
                            onClick={() => onDelete(hall.id)}
                        >
                            Delete
                        </button>
                    </div>

                    {showDailyInfo && dailyInfo && (
                        <div className="daily-info">
                            <h4>Daily Information</h4>
                            <p>{dailyInfo}</p>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default HallCard;
