import React, { useEffect } from 'react';
import { Link, Route, Routes } from 'react-router-dom';
import Halls from './halls/hall/Halls';
import HallDetail from './halls/HallDetail/HallDetail';
import Settings from './settings/Setting';
import Revenue from './revenue/Revenue';
import './Dashboard.css';

useEffect(() => {
  const handleResize = () => {
    setIsMobile(window.innerWidth <= 992);
    if (window.innerWidth > 992) {
      setIsMenuOpen(false);
    }
  };

  handleResize();
  window.addEventListener('resize', handleResize);
  return () => window.removeEventListener('resize', handleResize);
}, []);

const toggleMenu = () => {
  setIsMenuOpen(!isMenuOpen);
};

const Dashboard: React.FC = () => {
  return (
    <div className="dashboard">
      <aside className="sidebar">
        {/* Logo Section */}
        <div className="logo-section">
          <div className="logo">
            <span>VP</span>
          </div>
          <h1>VenuePro</h1>
        </div>

        <div className="sidebar-header">
          <h2>Management</h2>
        </div>
        <nav>
          <ul>
            <li>
              <Link to="/dashboard/halls">
                <span className="icon">üèõÔ∏è</span>
                <span className="text">Halls</span>
              </Link>
            </li>
            <li>
              <Link to="/dashboard/revenue">
                <span className="icon">üí∞</span>
                <span className="text">Revenue</span>
              </Link>
            </li>
            <li>
              <Link to="/dashboard/settings">
                <span className="icon">‚öôÔ∏è</span>
                <span className="text">Settings</span>
              </Link>
            </li>
          </ul>
        </nav>
        <div className="sidebar-footer">
          <div className="user-profile">
            <div className="avatar">üë§</div>
            <div className="user-info">
              <span className="name">Admin</span>
              <span className="role">Administrator</span>
            </div>
          </div>
        </div>
      </aside>
      <main className="content">
        <Routes>
          <Route path="halls/:hallId" element={<HallDetail />} />
          <Route path="halls" element={<Halls />} />
          <Route path="settings" element={<Settings />} />
          <Route path="revenue" element={<Revenue />} />
        </Routes>
      </main>
    </div>
  );
};

export default Dashboard;
